--1  Write a function to print numbers from 1 to 10. (Using while loop)	DECLARE @NO INT	SET @NO =1	BEGIN	WHILE(@NO <=10)	BEGIN		PRINT @NO		SET @NO = @NO +1	END	END--2 Write a function to check where a given number is ODD or EVEN.	DECLARE @N INT	SET @N = 12	BEGIN 	IF @N%2=0		PRINT 'EVEN NUMBER'	ELSE		PRINT 'ODD NUMBER'	END--3 Write a function to print ODD numbers between 1 and 10.	DECLARE @NUM INT	SET @NUM =1	BEGIN 	WHILE (@NUM<=10)	BEGIN 	IF @NUM%2=0		PRINT @NUM	SET @NUM = @NUM+1	END	END--4 Write a function to print Sum of numbers from 1 to 50.	DECLARE @A INT	DECLARE @SUM INT	SET @A=1	SET @SUM = 0	BEGIN	WHILE @A<=50	BEGIN		SET @SUM = @SUM + @A		SET @A = @A+1	END 	PRINT @SUM	END--5 Write a function to print Sum of even numbers between 1 to 20.	DECLARE @B INT	DECLARE @ESUM INT	SET @B =1	SET @ESUM =0	BEGIN 	WHILE @B<=20	BEGIN 	IF @B%2=0		SET @ESUM = @ESUM + @B	SET @B = @B + 1	END	PRINT @ESUM	END--6 Write a function to check whether a given number is prime or not.	DECLARE @X INT , @I INT, @FLAG INT	SET @X=7	SET @I=2	SET @FLAG=0	BEGIN	WHILE(@I<=@X/2)	BEGIN	IF (@X%@I=0)	BEGIN		SET @FLAG = 1		BREAK	END	SET @I = @I+1	END	IF @FLAG = 0		PRINT 'PRIME NUMBER'	ELSE		PRINT 'NOT PRIME NUMBER'	END--7Write a function for inserting even numbers into even table & odd numbers into odd table 
--between 1 to 50.	CREATE TABLE EVEN_NO(NUMBERS INT)	CREATE TABLE ODD_NO(NUMBERS INT)	DECLARE @Y INT	SET @Y=1	WHILE(@Y<=50)	BEGIN	IF(@Y%2=0)	BEGIN		INSERT INTO EVEN_NO VALUES(@Y)	END	ELSE		SET @Y=@Y+1	END		SELECT * FROM EVEN_NO	SELECT * FROM ODD_NO